package asd;

public class SEFSEFSEFS {

	public static void main(String[] args) {
		int x = 0;
		int sum=0;
		SSS s = new SSS();
		// TODO Auto-generated method stub
		for(int i=0; i<=4; i++) {
			x++;
			sum=sum +s.c();
		}
		System.out.println(sum);
	}

}
class SSS{
	static int x =0;
	
	int c() {
		return x+=2;
	}
}